import { Component, OnInit, resolveForwardRef } from '@angular/core';
import { interval } from 'rxjs';

@Component({
  selector: 'app-no-comunes',
  templateUrl: './no-comunes.component.html',

})
export class NoComunesComponent{
  //i18nPlural
  clientes: string[] = ['Maria', 'Pedro','Gabriel','Jorge'];
  pos : number = 0;

  clientesMapas = {
    '=0':'No tenemos ningun cliente esperando.',
    '=1':'Actualmente tenemos un cliente esperando.',
    '=2':'Actualmente tenemos dos cliente esperando.',
    'other': 'tenemos # clientes esperando.'
  }

  //i18nSelect
  nombre: string = 'gabriel';
  genero: string = 'masculino';

  // y se define el maping
  invitacionMapa = {
    'masculino': 'invitarlo',
    'femenino': 'invitarla'
  }

  cambiarCliente(){
    //this.nombre = this.clientes[this.pos];
    //console.log(this.nombre)

    //this.pos = this.pos + 1;

    this.nombre = 'Carla';
    this.genero= 'femenino';

  }

  borrarCliente(){

    this.clientes.pop();
    console.log(this.clientes.length)

  }

  // KeyValue Pipe , se crea obj literal

  persona = {
    nombre: 'Fernando',
    edad:35,
    direccion: 'Ottawa, Canada'
  }

  // JsonPipe

  heroes = [
    {
      nombre:'Superman',
      vuela:true
    },
    {
      nombre:'Robin',
      vuela:false
    },
    {
      nombre:'Aquaman',
      vuela:false
    }
  ]

  // Async Pipe 
  // se crea un obj observable
  miObservable = interval(1000); // valores del 0,1,2,3,4,5...

  valorPromesa = new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve('Tenemos data promesa')

    }, 3500);
  })
}
<div class="text-layout">
    <h1>Pipes no tan comunes</h1>
    <p>
        Pipes incluidos en Angular - Usualmente en el Common Moduel.
    </p>
</div>



<div class="p-grid">
    <div class="p-col p-md-6">
        <p-fieldset legend="i18nSelect Pipe" [toggleable] = "true">
           <p>
            Saludos {{nombre}}, es un placer
             {{genero | i18nSelect:invitacionMapa}}
             a nuestro evento.
            </p>
            <button pButton type="button" label="Cambiar Persona" class="p-button-success p-button-rounded" (click)="cambiarCliente()">

            </button>
        </p-fieldset>
    </div>

    <div class="p-col p-md-6">
        <p-fieldset legend="i18nPlural Pipe" [toggleable] = "true">
            <p>
            {{clientes.length | i18nPlural:clientesMapas}} cliente esperando.
            </p>


            <button pButton type="button" label="Borrar cliente" class="p-button-raised" (click)="borrarCliente()">

            </button>
        </p-fieldset>
    </div>

    <div class="p-col p-md-6">
        <p-fieldset legend="Slide Pipe" [toggleable] = "true">

            <b>Original</b>

            <pre>{{clientes}}</pre>

            <b>slice:0:2</b>
            <pre>{{clientes | slice:0:2}}</pre>
            <b>slice:1:2</b>
            <pre>{{clientes | slice:1:2}}</pre>

            <b>slice:1:3</b>
            <pre>{{clientes | slice:1:3}}</pre>

            <b>slice:0:4</b>
            <pre>{{nombre | slice:0:4}}</pre>


        </p-fieldset>
    </div>


    <div class="p-col p-md-6">
        <p-fieldset legend="KeyValue Pipe" [toggleable] = "true">
           <ul>
                <li *ngFor="let item of persona | keyvalue ">
                    <b>{{item.key | titlecase}}:</b> {{item.value}}
                </li>
           </ul>
        </p-fieldset>
    </div>

    <div class="p-col p-md-6">
        <p-fieldset legend="Json Pipe" [toggleable]="true">
            <pre>{{ heroes | json }}</pre>
        </p-fieldset>
    </div>

    <div class="p-col p-md-6">
        <p-fieldset legend="Async Pipe" [toggleable]="true">
            <pre>{{miObservable | async}}</pre>
        </p-fieldset>
    </div>


</div>
